import * as R from 'ramda'
import request from 'superagent'

import clothes from 'api/mockclothes'
import categories from 'api/mockCategories'
import size from 'api/mockSize'

export const fetchclothes = async () => {
  const {body} = await request.get(
    'http://www.mocky.io/v2/5e86179231000028008137fa'
  )
  return body.clothes
}

export const loadMoreclothes = async ({offset}) => {
  return new Promise(resolve => {
    resolve(clothes)
  })
}

export const fetchclotheById = async (id) => {
  return new Promise(resolve => {
    const clothe = R.find(R.propEq('id', id), clothes)
    resolve(clothe)
  })
}

export const fetchCategories = async () => {
  return new Promise(resolve => {
    resolve(categories)
  })
}

export const fetchSize = async () => {
  return new Promise(resolve => {
    resolve(size)
  })
}
