import React from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'
import {withRouter} from 'react-router'
import {compose} from 'redux'
import {getSize, getActiveSizeId} from 'selectors'
import classNames from 'classnames'
import * as R from "ramda";

const Size = ({size, activeSizeId}) => {
    console.log('activeSizeId ', activeSizeId)
    const renderSize = (size, index) => {
        const getActiveState = R.propEq('id', activeSizeId)
        const linkClass = classNames({
            'list-group-item': true,
            'active': getActiveState(size)
        })
            return (
                <Link
                    to={`/size/${size.id}`}
                    className={linkClass}
                    key={index}
                >
                    {size.name}
                </Link>
            )
    }
    const renderAllSize = () => {
        const linkClass = classNames({
            'list-group-item': true,
            'active': R.isNil(activeSizeId)
        })
        return (
            <Link
                to='/'
                className={linkClass}
            >
                All
            </Link>
        )
    }

    return(
        <div className='well'>
            <h4>Size</h4>
            <div className='list-group'>
                {renderAllSize()}
                {size.map((size, index) => renderSize(size, index))}
            </div>
        </div>
    )
}

const mapStateToProps = (state, ownProps) =>({
    size: getSize(state),
    activeSizeId: getActiveSizeId(ownProps)
})

export default compose(
    withRouter,
    connect(mapStateToProps, null)
)(Size)
