import {
  FETCH_clotheS_START,
  FETCH_clotheS_SUCCESS,
  FETCH_clotheS_FAILURE,
  LOAD_MORE_clotheS_FAILURE,
  LOAD_MORE_clotheS_START,
  LOAD_MORE_clotheS_SUCCESS,
  FETCH_clotheS_BY_ID_FAILURE,
  FETCH_clotheS_BY_ID_START,
  FETCH_clotheS_BY_ID_SUCCESS,
  ADD_clothe_TO_BASKET,
  SEARCH_clothe,
  FETCH_CATEGORIES_FAILURE,
  FETCH_CATEGORIES_START,
  FETCH_CATEGORIES_SUCCESS,
  FETCH_SIZE_FAILURE,
  FETCH_SIZE_START,
  FETCH_SIZE_SUCCESS,
  REMOVE_clothe_FROM_BASKET,
  CLEAN_BASKET
} from 'actionTypes'
import {
  fetchclothes as fetchclothesApi,
  loadMoreclothes as loadMoreclothesApi,
  fetchclotheById as fetchclotheByIdApi,
  fetchCategories as fetchCategoriesApi,
  fetchSize as fetchSizeApi
} from 'api'
import {getRenderedclothesLength} from 'selectors'

export const fetchclothes = () => async dispatch => {
  dispatch({type: FETCH_clotheS_START})

  try {
    const clothes = await fetchclothesApi()
    dispatch({
      type: FETCH_clotheS_SUCCESS,
      payload: clothes
    })
  } catch (err) {
    dispatch({
      type: FETCH_clotheS_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const loadMoreclothes = () => async (dispatch, getState) => {
  const offset = getRenderedclothesLength(getState())
  dispatch({type: LOAD_MORE_clotheS_START})

  try {
    const clothes = await loadMoreclothesApi({offset})
    dispatch({
      type: LOAD_MORE_clotheS_SUCCESS,
      payload: clothes
    })
  } catch (err) {
    dispatch({
      type: LOAD_MORE_clotheS_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const fetchclotheById = (id) => async dispatch => {
  dispatch({type: FETCH_clotheS_BY_ID_START})

  try {
    const clothe = await fetchclotheByIdApi(id)
    dispatch({
      type: FETCH_clotheS_BY_ID_SUCCESS,
      payload: clothe
    })
  } catch (err) {
    dispatch({
      type: FETCH_clotheS_BY_ID_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const addclotheToBasket = id => dispatch => {
  dispatch({
    type: ADD_clothe_TO_BASKET,
    payload: id
  })
}

export const searchclothe = text => dispatch => {
  dispatch({
    type: SEARCH_clothe,
    payload: text
  })
}

export const fetchCategories = () => async dispatch => {
  dispatch({type: FETCH_CATEGORIES_START})

  try {
    const categories = await fetchCategoriesApi()
    dispatch({
      type: FETCH_CATEGORIES_SUCCESS,
      payload: categories
    })
  } catch (err) {
    dispatch({
      type: FETCH_CATEGORIES_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const removeclotheFromBasket = id => async dispatch => {
  dispatch({
    type: REMOVE_clothe_FROM_BASKET,
    payload: id
  })
}

export const cleanBasket = () => async dispatch => {
  dispatch({
    type: CLEAN_BASKET
  })
}

export const basketCheckout = clothes => () => {
  alert(JSON.stringify(clothes))
}

export const fetchSize = () => async dispatch => {
  dispatch({type: FETCH_SIZE_START})

  try {
    const size = await fetchSizeApi()
    dispatch({
      type: FETCH_SIZE_SUCCESS,
      payload: size
    })
  } catch (err) {
    dispatch({
      type: FETCH_SIZE_FAILURE,
      payload: err,
      error: true
    })
  }
}
