import * as R from 'ramda'

import {
  FETCH_clotheS_SUCCESS,
  LOAD_MORE_clotheS_SUCCESS,
  SEARCH_clothe
} from 'actionTypes'

const initialState = {
  ids: [],
  search: ''
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_clotheS_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('id', payload)
      })
    case LOAD_MORE_clotheS_SUCCESS:
      const ids = R.pluck('id', payload)
      return R.merge(state, {
        ids: R.concat(ids, state.ids)
      })
    case SEARCH_clothe:
      return R.merge(state, {
        search: payload
      })
    default:
      return state;
  }
}